import React from 'react';

class Boundary extends React.Component {
    constructor(p){
        super(p);
        this.state = {
            error: false,
            errorMess: false
        }
    }

    // 專門用來接error的life cycle
    componentDidCatch(error, errorMess){
        console.log(error, errorMess)
    }

    render(){
        if (this.state.error === true){
            return(
                <h1>sth goes wrong.</h1>
            )
        }
        else{
            return(
                this.p.children
            )
        }
    }
}


export default Boundary;
