import React from 'react';
//import './App.css';
//import Card from './Card.js';
import CardList from './CardList.js';
import Search from './Search.js';
import Button from './Button.js';


class App extends React.Component{
  constructor(){
    super();
    this.state = {
      foods: [],
      SearchField: ''
    }
  }
// 1. listen to the changes of searchbox. 
//    if an event happens, then do this function
// 2. in render part, the element owns a property that carry this function
//    when it's triggered, execute the function
// 3. in search part, also, the search component needs a onChange attribute
// 4. the Search component and the App component communicate with props => {onChangeP} 
// 5. use value to get the value of event  
  keyInFood = (event) => {
    this.setState({SearchField: event.target.value}, () => console.log('keyInFood', this.state.SearchField));
    //this.setState({SearchField: event.target.value});
    // this.setState is async function, to console the right value, we need to use callback syntax.

  //   // also, match the keywords with foods[ ]
  //   const comparedFoods = this.state.foods.filter(food => {return food.shrt_desc.includes(this.state.SearchField)});
  //   this.setState.foods = comparedFoods;
  //   console.log('do comparison in keyInFood', this.state.SearchField); //所以，這邊就算setState，也不會Re-render
    }

  // fetch的function
  funFetch = () => {
    const foodWanted = this.state.SearchField;
    const url = 'https://food-calorie-data-search.p.rapidapi.com/api/search?keyword=' + foodWanted;
    fetch(url, 
      {
      "method": "GET",
      "headers": {
                    "x-rapidapi-host": "food-calorie-data-search.p.rapidapi.com",
                    "x-rapidapi-key": "2da75ce1cfmsh26fa14825a18dd4p16d23bjsn59e64460077f"
                  }
      }
    )
    .then(response => {
      return response.json()
    })
    .then(result => {
      //console.log(result[0].energ_kcal);
      this.setState({foods: result})
    })
    .catch(err => {
      console.log(err);
    });  
  }

//當載入頁面時，先fetch出apple的食物，
//當使用者key入文字，要把文字跟載入的[ ]做比對，並同步render在畫面上。

// CLICK button and SEARCH
  btnSearchFood = () => {
    console.log('btnOK', this.state.SearchField);
    this.funFetch();  
  }

// if user instead of clicking the button, presses enter => fetch the keyword
// 在input的地方要偵測是否按下enter(13) => 不是onChange, 不是onClick, 是onKeyDown
  enterSearchFood = (event) => {
    //console.log('enterSearchFood', event.keyCode, this.state.SearchField);
    if(event.keyCode===13){
      console.log('enterSearchFood', event.keyCode, this.state.SearchField);
      this.funFetch();  
    }
  }


// Render from the beginning  
  componentDidMount(){
    fetch("https://food-calorie-data-search.p.rapidapi.com/api/search?keyword=apple", 
          {
	        "method": "GET",
	        "headers": {
		                    "x-rapidapi-host": "food-calorie-data-search.p.rapidapi.com",
		                    "x-rapidapi-key": "2da75ce1cfmsh26fa14825a18dd4p16d23bjsn59e64460077f"
	                    }
          }
    )
    .then(response => {
	    return response.json()
    })
    .then(result => {
      //console.log(result[0].energ_kcal);
      this.setState({foods: result})
    })
    .catch(err => {
    	console.log(err);
    });
  }



// 點按其中一張Card -> 進到下一個畫面 -> 輸入重量, 勾勾存檔 -> 回到主畫面
// 主畫面：有日期、今日攝取食物清單及熱量、加入的搜尋畫面（目前做的）、今天的熱量加總顯示

  render(){
    // if I match the keywords with foods[ ] Here in the render part...
    const comparedFoods = this.state.foods.filter(food => {return food.shrt_desc.includes(this.state.SearchField)});
    this.setState.foods = comparedFoods;
    console.log('do comparison in render()', this.state.SearchField); 
    return(
      <div className='tc'>
        <div >
            <h1>Calorie Search</h1>
            <Search onChangeP={this.keyInFood} onKeyDownP={this.enterSearchFood}/>
            <Button onClickP={this.btnSearchFood}/>
        </div>
          <CardList foodP={this.state.foods}/>
      </div>
    )
  }
}

export default App;
